command:example.dev:code:
PS C:\>
 $data = @(
     @{
         Label = "Apple"
         Value = 12
         Color = 'Green'
     },
     @{
         Label = "Orange"
         Value = 54
         Color = 'Orange1'
     },
     @{
         Label = "Strawberry"
         Value = 51
         Color = 'Red'
     },
     @{
         Label = "Banana"
         Value = 33
         Color = 'purple'
     }
 )
$data | Format-SpectreBarChart -Title "My bar chart" -Width 35

PS C:\>
$Data = @(
     @{
         Label = "Apple"
         Value = 12
         Color = 'Green'
     },
     @{
         Label = "Strawberry"
         Value = 15
         Color = 'Red'
     },
     @{
         Label = "Orange"
         Value = 54
         Color = 'Orange1'
     },
     @{
         Label = "Plum"
         Value = 75
         Color = 'Fuchsia'
     }
 )
